def similarity(a, b):
    """Calculate similarity between two strings using SequenceMatcher"""
    return SequenceMatcher(None, a.lower(), b.lower()).ratio()

def find_cheelee_variations(text):
    """Find variations of 'cheelee' in text using fuzzy matching"""
    cheelee_variations = [
        'cheelee', 'chelee', 'chele', 'chile', 'chili', 'cheeli', 'chellee',
        'chillee', 'chealee', 'cheelie', 'chelly', 'cheely', 'chellie',
        'cheale', 'cheille', 'chiilee', 'cheelee', 'chelae',
        'ржЪрж┐рж▓рж┐', 'ржЪрж┐рж▓рзА', 'ржЪрж┐рж▓', 'ржЪрзАрж▓рж┐', 'ржЪрзЗрж▓рж┐', 'ржЪрзЗрж▓рзА', 'ржЪрж┐рж▓рзЗ', 'ржЪрзЗрж▓'
    ]

    words = text.lower().split()
    threshold = 0.7  # Minimum similarity threshold

    for word in words:
        # Clean word from punctuation
        clean_word = re.sub(r'[^\w\u0980-\u09FF]', '', word)

        # Check direct variations
        if clean_word in [var.lower() for var in cheelee_variations]:
            print(f"тЬЕ Direct Cheelee match found: {clean_word}")
            return True

        # Check fuzzy similarity with 'cheelee'
        if len(clean_word) >= 4:  # Only check words with 4+ characters
            if similarity(clean_word, 'cheelee') >= threshold:
                print(f"тЬЕ Fuzzy Cheelee match found: {clean_word} (similarity: {similarity(clean_word, 'cheelee'):.2f})")
                return True
            if similarity(clean_word, 'ржЪрж┐рж▓рж┐') >= threshold:
                print(f"тЬЕ Bengali Cheelee match found: {clean_word} (similarity: {similarity(clean_word, 'ржЪрж┐рж▓рж┐'):.2f})")
                return True

    return False

