def is_cheelee_related(text):
    """Check if the message is related to Cheelee using both exact matches and fuzzy matching"""
    # First check for fuzzy matching of 'cheelee' variations
    if find_cheelee_variations(text):
        return True

    # Then check for other Cheelee-related terms
    cheelee_terms = [
        'socialfi', 'earn money', 'ржЯрж╛ржХрж╛ ржЙржкрж╛рж░рзНржЬржи', 'ржЯрж╛ржХрж╛ ржЖржпрж╝',
        'glass', 'ржЧрзНрж▓рж╛рж╕', 'referral', 'рж░рзЗржлрж╛рж░рзЗрж▓', 'lee token', 'ржнрж┐ржбрж┐ржУ ржжрзЗржЦрзЗ ржЯрж╛ржХрж╛',
        'telegram', 'ржЯрзЗрж▓рж┐ржЧрзНрж░рж╛ржо', 'channel', 'ржЪрзНржпрж╛ржирзЗрж▓', 'group', 'ржЧрзНрж░рзБржк',
        'suspended', 'suspend', 'banned', 'ban', 'рж╕рзНржержЧрж┐ржд', 'ржмрзНржпрж╛ржи', 'ржЕрзНржпрж╛ржХрж╛ржЙржирзНржЯ рж╕рзНржержЧрж┐ржд',
        'refund', 'рж░рж┐ржлрж╛ржирзНржб', 'money back', 'compensation', 'ржХрзНрж╖рждрж┐ржкрзВрж░ржг', 'ржЯрж╛ржХрж╛ ржлрзЗрж░ржд', 'guarantee', 'ржЧрзНржпрж╛рж░рж╛ржирзНржЯрж┐', 'payback', 'reimburse'
    ]
    return any(term in text.lower() for term in cheelee_terms)

def is_question(text):
    """Check if the text is a question"""
    question_indicators = [
        '?', 'how', 'what', 'when', 'where', 'why', 'can', 'will', 'is',
        'ржХрж┐', 'ржХрж┐ржнрж╛ржмрзЗ', 'ржХрзАржнрж╛ржмрзЗ', 'ржХржЦржи', 'ржХрзЗржи', 'ржХрзЛржерж╛ржпрж╝', 'ржХрзАрж░рзВржкрзЗ'
    ]
    return any(indicator in text.lower() for indicator in question_indicators)

